    private void mudarNomeButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_mudarNomeButtonActionPerformed
        String nome = nomeTextField.getText();
        Usuario.atualizarNome(usuario, nome);
        usuario.setNome(nome);
    }//GEN-LAST:event_mudarNomeButtonActionPerformed

    private void voltarButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_voltarButtonActionPerformed
        Opcoes opcoes = new Opcoes();
        opcoes.setLocationRelativeTo(null);
        opcoes.manterUsuario(usuario);
        opcoes.setVisible(true);
        this.dispose();
    }//GEN-LAST:event_voltarButtonActionPerformed

    private void mudarSenhaButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_mudarSenhaButtonActionPerformed
        String senha = new String(senhaPasswordField.getPassword());
        Usuario.atualizarSenha(usuario, senha);
        usuario.setSenha(senha);
    }//GEN-LAST:event_mudarSenhaButtonActionPerformed

